import 'whatwg-fetch';

class HttpService{
   getProducts = () => {
      //1
      var promise = new Promise((resolve, reject) => {
         //3
         fetch('http://localhost:3004/product')
            .then(response => {
               //4
               resolve(response.json());
               reject("You suck!"); 

         })
      })
      //3
      return promise;

   }
}


export default HttpService;
